let _tauri_apps_api_core = require("@tauri-apps/api/core");

//#region guest-js/index.ts
/**
* Main `tauri-plugin-m3` Utility Class
*/
var M3 = class {
	/**
	* Get all inset properties for proper Edge-To-Edge Display
	* @example
	* ```javascript
	* import { M3 } from "tauri-plugin-m3";
	*
	* let insets = await M3.getInsets();
	* ```
	* @return A InsetsScheme object or false if unsuccessful
	*/
	static async getInsets() {
		try {
			return await (0, _tauri_apps_api_core.invoke)("plugin:m3|insets");
		} catch {
			return false;
		}
	}
	/**
	* Set the current status and navigation bar color
	* @example
	* ```javascript
	* import { M3 } from "tauri-plugin-m3";
	*
	* // either "dark", "light" or "system"
	* // default is "system"
	* let isSuccess = await M3.setBarColor("dark");
	* ```
	* @return A boolean indicating if successful or not
	*/
	static async setBarColor(color = "system") {
		try {
			return "error" in await (0, _tauri_apps_api_core.invoke)("plugin:m3|bar_color", { color }) ? false : true;
		} catch {
			return false;
		}
	}
	/**
	* Return the fetched ColorScheme
	* @example
	* ```javascript
	* import { M3 } from "tauri-plugin-m3";
	*
	* // either "dark", "light" or "system"
	* // default is "system"
	* let colorScheme = await M3.getColors("dark");
	* ```
	* @return A ColorScheme object or false if unsuccessful
	*/
	static async getColors(theme = "system") {
		try {
			let scheme = await (0, _tauri_apps_api_core.invoke)("plugin:m3|colors", { theme });
			if ("error" in scheme) return false;
			return scheme;
		} catch {
			return false;
		}
	}
	/**
	* Apply all colors in ColorScheme as CSS environment variables
	* @example
	* ```javascript
	* import { M3 } from "tauri-plugin-m3";
	*
	* // either "dark", "light" or "system"
	* // default is "system"
	* let isSuccess = await M3.applyColors("dark");
	* ```
	* @return A boolean indicating if successful or not
	*/
	static async applyColors(theme = "system") {
		try {
			let scheme = await (0, _tauri_apps_api_core.invoke)("plugin:m3|colors", { theme });
			if (!scheme || "error" in scheme) return false;
			for (const [varName, colorValue] of Object.entries(scheme)) document.documentElement.style.setProperty(`--${varName}`, colorValue);
			return true;
		} catch {
			return false;
		}
	}
};

//#endregion
exports.M3 = M3;